/* LIGHT MODE - Christmas Theme */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: 9;

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .06);

  --background: 0 0% 100%;

  --foreground: 0 0% 10%;

  --border: 140 20% 85%;

  --card: 0 0% 98%;

  --card-foreground: 0 0% 10%;

  --card-border: 140 20% 88%;

  --sidebar: 140 30% 96%;

  --sidebar-foreground: 0 0% 10%;

  --sidebar-border: 140 20% 90%;

  --sidebar-primary: 355 85% 50%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 140 40% 92%;

  --sidebar-accent-foreground: 0 0% 10%;

  --sidebar-ring: 355 85% 50%;

  --popover: 0 0% 100%;

  --popover-foreground: 0 0% 10%;

  --popover-border: 140 20% 88%;

  --primary: 355 85% 50%;

  --primary-foreground: 0 0% 100%;

  --secondary: 140 50% 45%;

  --secondary-foreground: 0 0% 100%;

  --muted: 140 20% 94%;

  --muted-foreground: 0 0% 40%;

  --accent: 45 95% 55%;

  --accent-foreground: 0 0% 10%;

  --destructive: 0 72% 48%;

  --destructive-foreground: 0 0% 100%;

  --input: 140 20% 50%;
  --ring: 355 85% 50%;
  --chart-1: 355 70% 60%;
  --chart-2: 140 60% 50%;
  --chart-3: 45 90% 60%;
  --chart-4: 355 50% 70%;
  --chart-5: 140 40% 65%;

  --font-sans: Inter, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --font-christmas: 'Great Vibes', cursive;
  --font-elegant: 'Playfair Display', serif;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 2px 0px 0px hsl(270 20% 20% / 0.05);
  --shadow-xs: 0px 2px 0px 0px hsl(270 20% 20% / 0.08);
  --shadow-sm: 0px 2px 0px 0px hsl(270 20% 20% / 0.05), 0px 1px 2px -1px hsl(270 20% 20% / 0.10);
  --shadow: 0px 2px 0px 0px hsl(270 20% 20% / 0.05), 0px 1px 2px -1px hsl(270 20% 20% / 0.10);
  --shadow-md: 0px 2px 0px 0px hsl(270 20% 20% / 0.05), 0px 2px 4px -1px hsl(270 20% 20% / 0.12);
  --shadow-lg: 0px 2px 0px 0px hsl(270 20% 20% / 0.05), 0px 4px 6px -1px hsl(270 20% 20% / 0.14);
  --shadow-xl: 0px 2px 0px 0px hsl(270 20% 20% / 0.05), 0px 8px 10px -1px hsl(270 20% 20% / 0.16);
  --shadow-2xl: 0px 2px 0px 0px hsl(270 20% 20% / 0.20);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 140 30% 8%;

  --foreground: 0 0% 95%;

  --border: 140 20% 18%;

  --card: 140 25% 12%;

  --card-foreground: 0 0% 95%;

  --card-border: 140 20% 16%;

  --sidebar: 140 25% 10%;

  --sidebar-foreground: 0 0% 95%;

  --sidebar-border: 140 20% 15%;

  --sidebar-primary: 355 85% 55%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 140 25% 18%;

  --sidebar-accent-foreground: 0 0% 90%;

  --sidebar-ring: 355 85% 55%;

  --popover: 140 25% 12%;

  --popover-foreground: 0 0% 95%;

  --popover-border: 140 20% 20%;

  --primary: 355 85% 55%;

  --primary-foreground: 0 0% 100%;

  --secondary: 140 30% 25%;

  --secondary-foreground: 0 0% 95%;

  --muted: 140 20% 20%;

  --muted-foreground: 0 0% 65%;

  --accent: 45 90% 50%;

  --accent-foreground: 0 0% 10%;

  --destructive: 0 72% 48%;

  --destructive-foreground: 0 0% 100%;

  --input: 140 20% 35%;
  --ring: 355 85% 55%;
  --chart-1: 355 70% 60%;
  --chart-2: 140 60% 50%;
  --chart-3: 45 90% 60%;
  --chart-4: 355 50% 70%;
  --chart-5: 140 40% 65%;

  --shadow-2xs: 0px 2px 0px 0px hsl(270 20% 5% / 0.15);
  --shadow-xs: 0px 2px 0px 0px hsl(270 20% 5% / 0.20);
  --shadow-sm: 0px 2px 0px 0px hsl(270 20% 5% / 0.15), 0px 1px 2px -1px hsl(270 20% 5% / 0.25);
  --shadow: 0px 2px 0px 0px hsl(270 20% 5% / 0.15), 0px 1px 2px -1px hsl(270 20% 5% / 0.25);
  --shadow-md: 0px 2px 0px 0px hsl(270 20% 5% / 0.15), 0px 2px 4px -1px hsl(270 20% 5% / 0.30);
  --shadow-lg: 0px 2px 0px 0px hsl(270 20% 5% / 0.15), 0px 4px 6px -1px hsl(270 20% 5% / 0.35);
  --shadow-xl: 0px 2px 0px 0px hsl(270 20% 5% / 0.15), 0px 8px 10px -1px hsl(270 20% 5% / 0.40);
  --shadow-2xl: 0px 2px 0px 0px hsl(270 20% 5% / 0.50);

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
  }

  html.dark {
    color-scheme: dark;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {
  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* Christmas Background Animations */
  @keyframes float {
    0%, 100% {
      transform: translateY(0) rotate(0deg);
      opacity: 0.3;
    }
    50% {
      transform: translateY(-30px) rotate(180deg);
      opacity: 0.8;
    }
  }

  @keyframes gentle-scale {
    0%, 100% {
      transform: scale(1);
    }
    50% {
      transform: scale(1.05);
    }
  }

  @keyframes twinkle {
    0%, 100% {
      opacity: 0.2;
      transform: scale(0.8);
    }
    50% {
      opacity: 1;
      transform: scale(1.2);
    }
  }

  @keyframes pulse-glow {
    0%, 100% {
      opacity: 0.3;
      filter: blur(0px);
    }
    50% {
      opacity: 0.8;
      filter: blur(2px);
    }
  }

  @keyframes gentle-pulse {
    0%, 100% {
      opacity: 0.3;
    }
    50% {
      opacity: 0.6;
    }
  }

  @keyframes float {
    0%, 100% {
      transform: translateY(0px);
    }
    50% {
      transform: translateY(-10px);
    }
  }

  .animate-gentle-scale {
    animation: gentle-scale 3s ease-in-out infinite;
  }

  /* Hide scrollbar but keep functionality */
  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }

  /* Christmas cursor with red outline */
  * {
    cursor: auto;
    cursor: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='24' height='24' viewBox='0 0 24 24'><path d='M3 3 L3 18 L9 12 L12 21 L14 20 L11 11 L18 11 Z' fill='white' stroke='%23dc2626' stroke-width='2'/></svg>") 3 3, auto;
  }

  a, button, [role="button"], input[type="submit"], input[type="button"], .cursor-pointer {
    cursor: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='24' height='24' viewBox='0 0 24 24'><path d='M6 3 L6 16 L10 12 L13 19 L15 18 L12 11 L18 11 Z' fill='white' stroke='%23dc2626' stroke-width='2'/></svg>") 6 3, pointer;
  }
}

